import { FC, useState } from "react"

interface Iprops {
    nameForm?:string
}

const Login:FC<Iprops> = (props : Iprops) => {
    const [email,setEmail] = useState<string>('');
    const [errors,setErrors] = useState<string[]>([]);
    const handeEmail = (event : React.ChangeEvent<HTMLInputElement>) => {

        event.preventDefault(); 
        const value = event.target.value;

        if(!value || !value.length){

            setErrors(pre=>{
                pre.push('Email is not empty');
                return pre;
            })
        } else if (value.length < 6) {

            setErrors(pre=> {
                pre.push('Email is not length 6 ky tu');
                return pre;
            })
        } else {
            setErrors([]);
        }
    }
    return (
        <>
            <h1>{props.nameForm}</h1>
            <form>
                Email <input type="text" onChange={handeEmail}/>
                {
                    errors.map((err, index) => {
                        return <p key={index}>{err}</p>
                    })
                }
            </form>
        </>
    );

}  
export default Login;
