import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { RootState } from "../toolkit";
import { decrement, increment } from "../toolkit/counter/counterSlice";
import { useGetStudenListQuery } from "../toolkit/student/student.service";
import { IStudent } from "../toolkit/student/student.interface";
import { fetchList } from "../toolkit/student/studentSlice";

const Counter = () => {
    // const [counter,setCounter] = useState(0);
    // const handlerCounter = () => {
    //     setCounter(counter - 1);
    // }

    const count = useSelector((state: RootState) => state.counter.value)
    const dispatch = useDispatch()

    const { isError, isLoading, data: studentList, isSuccess: studentListSuccess, refetch } = useGetStudenListQuery(null);
    if (isLoading) {
        return <p>Loading ...</p>
    }
    if (isError) {
        return <p>Error</p>
    }

    useEffect(() => {
        if (studentListSuccess) {
            dispatch(fetchList(studentList))
        }
    }, [studentListSuccess])

    return (
        <div>
            <ul>
                {
                    (studentList as IStudent[] || []).map(student => {
                        return <li key={student.id}>{student.ten}</li>
                    })
                }
            </ul>

            <button>Add</button>
            <button onClick={refetch}>Refetch</button>

            {/* <div>
                <button
                    aria-label="Increment value"
                    onClick={() => dispatch(increment())}
                >
                    Increment
                </button>
                <span>{count}</span>
                <button
                    aria-label="Decrement value"
                    onClick={() => dispatch(decrement())}
                >
                    Decrement
                </button>
            </div> */}
        </div>
    )
}
export default Counter;
